import React, { useState } from "react";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
function App() {


  const [mode,setMode] = useState("light")

  const toggleMode =()=>{
      if(mode==="light"){
        setMode("dark")
      }else{
        setMode("light")
      }
  }
  return (
   <>
    <Navbar title="TextConverter"  mode={mode} toggleMode={toggleMode} />
    <TextForm/>
   </>
  );
}

export default App;
