import React from 'react'
import { useState } from 'react'

function TextForm() {

    const [text, setText] = useState("")

    const handleChange = (e)=>{
        setText(e.target.value)
    }

    const  handleClick = ()=>{
        setText(text.toUpperCase())
    }
    return (
        <div className='container'>
            <div className="mt-5">
                <textarea className="form-control" onChange={handleChange} value={text} placeholder="Enter Text here" rows="10" ></textarea>
            </div>
            <button type="button" onClick={handleClick} className="btn mt-3 btn-primary">Convert to Uppercase</button>
        </div>
    )
}

export default TextForm
